import React from 'react';
import { Routes, Route, Link, NavLink, useNavigate, useParams, Outlet } from 'react-router-dom';
import './App.css';
import Person from './data/people.json';
import Movies from './data/films.json';
import Worlds from './data/planets.json';

function App() {

  return (
    <div>
      <Nav/>
      <SideNav/>
      <Routes>
        <Route path="/" element={<Home/>}/>
        <Route path="/people">
          <Route path=":id" element={<PeopleData/>}/>
        </Route>
        <Route path="/planets">
          <Route path=":id" element={<Planets/>}/>
        </Route>
        <Route path="/film" element={<Film/>}>
          <Route path=":id" element={<Film/>}/>
        </Route>
        <Route path="*" element={<Error/>}/>
      </Routes>
    </div>
  );
}



function Home(){
  return(
    <div>
      <h1>Hello! Here, you can look up more information about your favorite Star Wars person, planet or film! Please select from the navbar!</h1>
    </div>
  );
}

function PeopleData(){

  const people_array = [];
  const pageID = useParams();
  const person = {};
  Person.map = people_array;
  //for(let id of Object.keys(Person)){
    //people_array.push(<li key = {id}>{Person[id]}</li>)
  //}
  if(pageID.id in Person[pageID.id]){
    person = Person[pageID.id];
  }

  return(
      <div>
          <h1>{Person[pageID.id].name}</h1>
          <p>Height: {Person[pageID.id].height}</p>
          <p>Mass: {Person[pageID.id].mass}</p>
          <p>Hair Color: {Person[pageID.id].hair_color}</p>
          <p>Skin Color: {Person[pageID.id].skin_color}</p>
          <p>Eye Color: {Person[pageID.id].eye_color}</p>
          <p>Birth year: {Person[pageID.id].birth_year}</p>
          <p>Gender: {Person[pageID.id].gender}</p>
          <p>Home World: <Link to={{pathname:Person[pageID.id].homeworld}}>{Person[pageID.id].homeworld}</Link></p>
          <p>Films: {Person[pageID.id].films.map((value) => (
            <li key={value}><Link to ={value}>{value}</Link></li>
          ))}</p>
      </div>   
  );
}
//<Planets to={Person[pageID.id].homeworld}>World</Planets>
function Planets(){

  const planet_array = [];
  const pageID = useParams();
  const planet = {};
  Worlds.map = planet_array;
  //for(let id of Object.keys(Person)){
    //people_array.push(<li key = {id}>{Person[id]}</li>)
  //}
  if(pageID.id in Worlds[pageID.id]){
    planet = Worlds[pageID.id];
  }

  return(
      <div>
          <h1>{Worlds[pageID.id].name}</h1>
          <p>Height: {Worlds[pageID.id].rotation_period}</p>
          <p>Mass: {Worlds[pageID.id].orbital_period}</p>
          <p>Hair Color: {Worlds[pageID.id].diameter}</p>
          <p>Skin Color: {Worlds[pageID.id].climate}</p>
          <p>Eye Color: {Worlds[pageID.id].gravity}</p>
          <p>Birth year: {Worlds[pageID.id].terrain}</p>
          <p>Gender: {Worlds[pageID.id].surface_water}</p>
          <p>Population: {Worlds[pageID.id].population}</p>
          <p>Residents: {Worlds[pageID.id].residents.map((value) => (
            <li key={value}><Link to ={value}>{value}</Link></li>
          ))}</p>
          <p>Films: {Worlds[pageID.id].films.map((value) => (
            <li key={value}><Link to ={value}>{value}</Link></li>
          ))}</p>
      </div>
  );
}
function Film(){
  return(
      <div>
          <h1>Hi Film</h1>
      </div>
  );
}
function Nav(){
  return(
    <div>
      <Link to="/">Star Wars Home</Link>
      <Link to="/people">People</Link>
      <Link to="/film">Films</Link>
      <Link to="/planets">Planets</Link>
    </div>
  );
}

function Error(){
  return(
    <div>
      <h1>This link does not exist, please hit back or choose from one of the options of the navbar</h1>
    </div>
  )
}

function SideNav(){
  const people_array = [];
  const planet_array = [];
  const films_array = [];
  const pageID = useParams();




  return(
    <div>

    </div>
  );
}

export default App;
