import React from 'react';
import { Routes, Route, Link, NavLink, useNavigate, useParams } from 'react-router-dom';
import './App.css';
import Person from './data/people.json';
import Movies from './data/films.json';
import Worlds from './data/planets.json';

function App() {

  return (
    <div>
      <Nav/>
      <Routes>
        <Route path="/" element={<Home/>}/>
        <Route path="/people/:id" element={<People/>}/>
        <Route path="/planet/:id" element={<Planets/>}/>
        <Route path="/film/:id" element={<Films/>}/>
        <Route path="/404" element={<Error/>}/>
      </Routes>
    </div>
  );
}

function Home(){
  return(
    <div>
      <h1>Hello! Here, you can look up more information about your favorite Star Wars person, planet or film! Please select from the navbar!</h1>
    </div>
  );
}

function People(){

  const people_array = [];
  const pageID = useParams();
  const person = {};
  let navigate = useNavigate();
  Person.map = people_array;
  //for(let id of Object.keys(Person)){
    //people_array.push(<li key = {id}>{Person[id]}</li>)
  //}
  console.log(pageID.id);
  console.log(Person[pageID.id]);
  if(pageID.id in Person[pageID.id]){
    person = Person[pageID.id];
  }//else error page
  console.log(person);
  return(
      <div>
          <h1>{Person[pageID.id].name}</h1>
          <p>Height: {Person[pageID.id].height}</p>
          <p>Mass: {Person[pageID.id].mass}</p>
          <p>Hair Color: {Person[pageID.id].hair_color}</p>
          <p>Skin Color: {Person[pageID.id].skin_color}</p>
          <p>Eye Color: {Person[pageID.id].eye_color}</p>
          <p>Birth year: {Person[pageID.id].birth_year}</p>
          <p>Gender: {Person[pageID.id].gender}</p>
          <p>Home World: <Link to={{
            pathname: "/planet"
            }}>{Person[pageID.id].homeworld}</Link></p>

      </div>   
  );
}

//<Planets to={Person[pageID.id].homeworld}>World</Planets>

function Planets(){
  return(
      <div>
          <h1>Hi Planets</h1>
      </div>
  );
}
function Films(){
  return(
      <div>
          <h1>Hi Film</h1>
      </div>
  );
}
function Nav(){
  return(
    <div>
      <Link to="/">Star Wars Home</Link>
      <Link to="/people">People</Link>
      <Link to="/film">Films</Link>
      <Link to="/planet">Planets</Link>
    </div>
  );
}

function Error(){
  return(
    <div>
      <h1>This link does not exist, please hit back or choose from one of the options of the navbar</h1>
    </div>
  )
}

export default App;
