import React from 'react';
import { Routes, Route, Link, useParams, Outlet } from 'react-router-dom';
import './App.css';
import Person from './data/people.json';
import Movies from './data/films.json';
import Worlds from './data/planets.json';

function App() {

  return (
    <div>
      <Nav className="Nav"/>
      <div>
      <Routes>
        <Route path="/" element={<Home/>}/>
        <Route path="/people" element={<People/>}>
          <Route path=":id" element={<PeopleData/>}/>
        </Route>
        <Route path="/planets" element={<Planets/>}>
          <Route path=":id" element={<PlanetsData/>}/>
        </Route>
        <Route path="/films" element={<Film/>}>
          <Route path=":id" element={<FilmData/>}/>
        </Route>
        <Route path="*" element={<Error/>}/>
      </Routes>
      </div>
    </div>
  );
}



function Home(){
  return(
    <div>
      <h1>Hello! Here, you can look up more information about your favorite Star Wars person, planet or film! Please select from the navbar!</h1>
    </div>
  );
}

function People(){
  return(
    <div>
      <h1>Displaying this Character from sidebar selection!</h1>
      <div>
          {Object.entries(Person).map(([key, value]) => (
             <li key={key}><Link to ={value.url}>{value.name}</Link></li>
          ))}
      </div>
      <Outlet/>
    </div>
  )
}
function Planets(){
  return(
    <div>
      <h1>Displaying this Planet from sidebar selection!</h1>
      <div>
        {Object.entries(Worlds).map(([key, value]) => (
          <li key={key}><Link to ={value.url}>{value.name}</Link></li>
        ))}
      </div>
      <Outlet/>
    </div>
  )
}
function Film(){
  return(
    <div>
      <h1>Displaying this Film from sidebar selection!</h1>
      <div>
        {Object.entries(Movies).map(([key, value]) => (
          <li key={key}><Link to ={value.url}>{value.title}</Link></li>
        ))}
      </div>
      <Outlet/>
    </div>
  )
}
function PeopleData(){

  const people_array = [];
  const pageID = useParams();
  const person = {};
  Person.map = people_array;
  if(pageID.id in Person[pageID.id]){
    person = Person[pageID.id];
  }

  return(
